  var createdAt = Date.now() - randomInt(TEN_DAYS_AGO);
  return new Date(createdAt).toISOString();
};

var randomUpdatedAtDate = function randomUpdatedAtDate(createdAt) {
  var now = Date.now();
  createdAt = createdAt || new Date(now).toISOString();
  var range = now - new Date(createdAt);
  var updatedAt = now - randomInt(range);
